import { useState } from 'react'



const Tasks = () => {
    const [tasks, setTasks] = useState([])
    const onClick = () => {
    setTasks([
        ...tasks, {name: "Leonard"}
    ]
    )
    }

    return (
        <div>
        <h1 onClick = {onClick}>This is data: { tasks[0].name }</h1>
        {tasks.map((task) => (<h3>{task.name}</h3>))}

        </div>
    )
}

export default Tasks
