import { useState, useEffect } from 'react'
import Tasks from './Tasks';
import NewTask from './tasks/NewTask';
import SectionsList from './SectionsList';


const Home = () => {
    const [tasks, setTasks] = useState([])
    const [values, setValues] = useState([])

    useEffect(() =>{
       const getTasks = async () => {
           const tasksFromServer = await fetchData()
           setTasks(tasksFromServer)
       }
        getTasks()
    }, [])
    //get Data
    const fetchData = async () => {
        const res = await fetch('http://localhost:8000')
        const data = await res.json()
        return data
    }
    const sendTask = async (task) => {
      const options = {
        method: 'POST',
        body: JSON.stringify( task )  

    };
      const res = await fetch('http://localhost:8000')
      const data = await res.json()
      return data
    }
    const getLabel = (name) => {
        for(var i = 0;i<sections.length;i++){
            if(sections[i].name == name){
                return sections[i]
                break
            }

        }

       }
    const submitTask = () => {
        if(document.getElementById('taskName').value && document.getElementById('taskSection').value){
            const labelName = document.getElementById('taskSection').value
          const newTask = {name: document.getElementById('taskName').value, tag : document.getElementById('taskSection').value,backgroundColor : getLabel(labelName).backgroundColor, textColor : getLabel(labelName).backgroundColor}
          setTasks([
            ...tasks, newTask
        ]
        )

        }

      }
      const [sections, setSections] = useState([])

      useEffect(() =>{
         const getSections = async () => {
             const sectionsFromServer = await getSectionsServer()
             setSections(sectionsFromServer)
         }
          getSections()
      }, [])
      //get Data
      const getSectionsServer = async () => {
          const res = await fetch('http://localhost:8000/sections')
          const data = await res.json()
          return data
      }
    return (
        <div>
            <NewTask submitTask = { submitTask } sections = { sections }>
            </NewTask>
            <Tasks tasks ={ tasks }></Tasks>

        </div>
    )
}

export default Home
